import { AfterViewInit, Component, ViewChild, TemplateRef, ElementRef } from '@angular/core';
import { AuthService } from '../../servicios/auth/auth.service';

//Componentes de angular material
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatCardModule } from '@angular/material/card';
import { MatSort, MatSortModule } from '@angular/material/sort';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatPaginator, MatPaginatorModule } from '@angular/material/paginator';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';
import { MatInputModule } from '@angular/material/input';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';
import { MatDividerModule } from '@angular/material/divider';

//Bootstrap
import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';
import { BsDatepickerModule, BsDatepickerConfig, BsLocaleService } from 'ngx-bootstrap/datepicker';

//Servicios
import { FuncionesValidate } from '../../servicios/funciones-validate.service';
import { HistorialClinico } from '../../interface/historial-clinico';
import { Citas } from './../../interface/citas';
import { Medico } from '../../interface/medico';

import jsPDF from 'jspdf';
import { Paciente } from '../../interface/paciente';

@Component({
  selector: 'app-gestor-citas',
  standalone: true,
  imports: [
    MatCardModule,
    MatButtonModule,
    MatTableModule,
    MatPaginatorModule,
    MatFormFieldModule,
    MatInputModule,
    MatSortModule,
    MatIconModule,
    MatDividerModule,
    MatSelectModule,
    BsDatepickerModule
  ],
  templateUrl: './gestor-citas.component.html',
  styleUrl: './gestor-citas.component.css'
})


export class GestorCitasComponent implements AfterViewInit {
  //Columnas de la tabla citas
  displayedColumns: string[] = ['id_cita', 'historial', "receta", 'horario', 'fecha', 'datos_adicionales', 'estado', 'tipo_cita'];
  //Columnas de la tabla
  historialColumns: string[] = ['id_historial', 'titulo', 'descripcion', 'imagen', 'documento'];
  //Interfaz de los datos del historial clinico
  dataSource = new MatTableDataSource<Citas>();
  //Modal
  modalRef!: BsModalRef;
  //Nombre del paciente
  nombre: string = "";
  //id del paciente
  id_paciente: string = "";
  //Id cita
  id_cita: string = "";
  //Id consultorio
  id_consultorio: string = "";
  //id medico
  id_medico = ''
  //Numero del registro
  numRH: string = "";
  //Nombre del registro
  nomRH: string = "";
  //Imagen model
  imagenModal: string = "";
  //Interfaz de los datos del historial clinico
  historialSource = new MatTableDataSource<HistorialClinico>();
  //Datepiker
  bsConfig!: Partial<BsDatepickerConfig>;
  //Datos del medico
  medico: Medico | null = null;
  //Fecha del formulario de la receta
  @ViewChild('fechaCita') fechaCita!: ElementRef<HTMLInputElement>;
  //Interfaz de los datos del paciente
  pacienteReceta: Paciente | null = null;

  //Constructor
  constructor(private snackBar: MatSnackBar, private modalService: BsModalService, private localeService: BsLocaleService, private authService: AuthService, private valf: FuncionesValidate) {
    //Obtiene la fecha actual
    var date = new Date();
    //Configuracion del datepiker
    this.bsConfig = {
      containerClass: 'theme-dark-blue',//Tema del datepiker
      dateInputFormat: 'YYYY-MM-DD',//Formato de la fecha
      minDate: date
    };
    //Cambia el idioma del datepiker
    this.localeService.use('es');

    this.id_medico = this.authService.getId() + "";
  }

  //Referencia a los elementos de angular material
  //Paginador de la tabla
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  //Sort de la tabla
  @ViewChild(MatSort) sort!: MatSort;


  //Una vez se carga toda la vista se ejecuta
  //
  //
  ngAfterViewInit() {
    //Paginador
    this.dataSource.paginator = this.paginator;
    //Sort
    this.dataSource.sort = this.sort;
    this.sort.sort({ id: 'id_cita', start: 'desc', disableClear: false }); // Orden descendente por defecto
    //Se carga las citas
    this.informacion();
  }

  //Obtiene las citas del consultorio
  //
  //
  async informacion() {
    try {
      const response = await fetch('http://localhost/api/consultorio/getcitasmedico?id_medico=' + this.id_medico, {
        method: 'GET',
        credentials: 'include',
        headers: { 'Origin': window.location.origin }
      });
      //Verifica que la respuesta sea correcta
      if (response.ok) {
        //Se obtienen los datos de la respuesta
        var result = await response.json();
        //Lista los datos en la tabla
        this.dataSource.data = result;
      } else {
        this.alerta("Error al obtener el registro de las citas.")
      }
    } catch (error) {
      console.error('Error POST:', error);
    }
  }

  //Abre el modal para ver el historial del paciente
  //
  //
  openModal(template: TemplateRef<any>, id_paciente: string, nombre_paciente: string, id_consultorio: string) {
    //Datos que se mostraran en el modal
    this.nombre = nombre_paciente;
    this.id_paciente = id_paciente;
    this.id_consultorio = id_consultorio;
    //Se mandan a traer los datos del paciente
    this.infoHistorial();
    //Abre el modal
    this.modalRef = this.modalService.show(template, { class: 'modal-xl', backdrop: false });//se le agrega la clase modal xl para hacer el modal grande
  }


  //Abre el modal y cambia la imagen del modal
  //
  //
  showImageModal(template: TemplateRef<any>, id_historial: string, titulo: string, imagen: string) {
    //Datos que se mostraran en el modal
    this.numRH = id_historial;
    this.nomRH = titulo;
    this.imagenModal = "data:image/png;base64," + imagen;
    //Abre el modal
    this.modalRef = this.modalService.show(template, { class: 'modal-xl' });//se le agrega la clase modal xl para hacer el modal grande
  }


  //Busqueda en la tabla esta funcion es la proporcionada en la documentacion de angular material
  //
  //
  applyFilter(event: Event) {
    //Obtiene el valor del input de busqueda en la tabla
    const filterValue = (event.target as HTMLInputElement).value;
    //Quita el texto en balnco al inicio y al final y lo pasa a minusculas
    this.dataSource.filter = filterValue.trim().toLowerCase();

    if (this.dataSource.paginator) {
      this.dataSource.paginator.firstPage();
    }
  }

  //Registro de la receta
  //
  //
  async registro(event: Event, estatura: string, peso: string, diagnostico: string, fecha: string, alergias: string, temperatura: string, tratamiento: string, t_a: string) {
    //Evita que se actualice la pagina
    event.preventDefault();
    //Valida que al menos se coloque el nombre y descripcion
    if (!this.valf.empty(estatura) && !this.valf.empty(peso) && !this.valf.empty(diagnostico) && !this.valf.empty(fecha) && !this.valf.empty(alergias) && !this.valf.empty(temperatura) && !this.valf.empty(tratamiento)) {

      //Obtiene los datos del paciente
      try {
        //Manda la peticion de los datos
        var response = await fetch('http://localhost/api/paciente/getinfo?id_paciente=' + this.id_paciente, {
          method: 'GET',
          credentials: 'include',
          headers: { 'Origin': window.location.origin }
        });

        //Verifica que la respuesta sea correcta
        if (response.status == 200) {
          var result = await response.json();
          this.pacienteReceta = result as Paciente;
        } else {
          this.alerta(result.message);
        }
      } catch (error) {
        console.error('Error POST:', error);
      }

      //Fecha de nacimiento del paciente
      var fechaNacimientoPaciente = this.pacienteReceta?.fecha_nacimiento

      var edad = '';

      //Obtiene la edad del paciente
      //
      //
      // Verificar que fechaNacimientoPaciente no es undefined 
      if (fechaNacimientoPaciente) {
        //Llama al meotodo para obtener la edad
        edad = this.calcularEdad(fechaNacimientoPaciente);
      } else {
        console.log("La fecha de nacimiento es undefined");
      }

      //Se crea el form data para mandar los parametros
      var formData = new FormData();
      formData.append('id_paciente', this.id_paciente);
      formData.append('id_medico', this.id_medico);
      formData.append('id_consultorio', this.id_consultorio);
      formData.append('edad', edad);
      formData.append('estatura', estatura);
      formData.append('peso', peso);
      formData.append('diagnostico', diagnostico);
      formData.append('fecha', fecha);
      formData.append('alergias', alergias);
      formData.append('temperatura', temperatura);
      formData.append('tratamiento', tratamiento);
      formData.append('t_a', t_a);

      try {
        //registro
        var response = await fetch('http://localhost/api/consultorio/recetasave', {
          method: 'POST',
          body: formData,
          credentials: 'include',
          headers: { 'Origin': window.location.origin }
        });

        //Obtiene los datos del medico para la receta
        try {
          //Manda la peticion de los datos
          var response = await fetch('http://localhost/api/medico/getinfo?id_medico=' + this.id_medico, {
            method: 'GET',
            credentials: 'include',
            headers: { 'Origin': window.location.origin }
          });

          //Verifica que la respuesta sea correcta
          if (response.status == 200) {
            var result = await response.json();
            this.medico = result as Medico;

            //Obtjeto de jsPDF para crear el PDF
            var doc = new jsPDF();
            // Ajusta las dimensiones según sea necesario 
            // Agregar datos de la receta

            //Tamaño de la letra
            doc.setFontSize(11);

            var image = 'data:image/png;base64,';
            // Crear instancia de jsPDF
            var doc = new jsPDF();

            // Agregar imagen
            doc.addImage(image, 'png', 10,  5, 20, 20);

            // Tamaño de la letra
            doc.setFontSize(11);
            // Texto
            //
            //
            //Doctor
            doc.setFont("helvetica", "bold");
            doc.text('Doctor: ', 90, 15);
            doc.setFont("helvetica", "normal");
            doc.text(this.medico.nombre + ' ' + this.medico.apellidos, 105, 15);

            //Cédula Profesional
            doc.setFont("helvetica", "bold");
            doc.text('Cédula Profesional: ', 80, 25);
            doc.setFont("helvetica", "normal");
            doc.text(this.medico.cedula, 117, 25);

            //Líneas de separación
            doc.text('__________________________________________________________________________________________________________________________________________________________________________', 0, 29);
            doc.text('__________________________________________________________________________________________________________________________________________________________________________', 0, 30);

            //Paciente
            doc.setFont("helvetica", "bold");
            doc.text('Paciente: ', 10, 45);
            doc.setFont("helvetica", "normal");
            doc.text(this.nombre + ' ' + this.pacienteReceta?.apellidos, 29, 45);

            //Edad
            doc.setFont("helvetica", "bold");
            doc.text('Edad: ', 10, 55);
            doc.setFont("helvetica", "normal");
            doc.text(edad, 22, 55);

            //Alergias
            doc.setFont("helvetica", "bold");
            doc.text('Alergias: ', 10, 65);
            doc.setFont("helvetica", "normal");
            doc.text(alergias, 29, 65);

            //Estatura
            doc.setFont("helvetica", "bold");
            doc.text('Estatura: ', 10, 75);
            doc.setFont("helvetica", "normal");
            doc.text(estatura, 29, 75);

            //Peso
            doc.setFont("helvetica", "bold");
            doc.text('Peso: ', 10, 85);
            doc.setFont("helvetica", "normal");
            doc.text(peso + ' Kg', 25, 85);

            //Temperatura
            doc.setFont("helvetica", "bold");
            doc.text('Temperatura: ', 10, 95);
            doc.setFont("helvetica", "normal");
            doc.text(temperatura + ' °C', 35, 95);

            //T/A
            doc.setFont("helvetica", "bold");
            doc.text('T/A: ', 10, 105);
            doc.setFont("helvetica", "normal");
            doc.text(t_a, 20, 105);

            //Fecha
            doc.setFont("helvetica", "bold");
            doc.text('Fecha: ', 10, 115);
            doc.setFont("helvetica", "normal");
            doc.text(fecha, 25, 115);

            //Diagnostico
            doc.setFont("helvetica", "bold");
            doc.text('Diagnostico: ', 10, 125);
            doc.setFont("helvetica", "normal");
            doc.text(diagnostico, 35, 125);

            //Tratamiento
            doc.setFont("helvetica", "bold");
            doc.text('Tratamiento: ', 10, 135);
            doc.setFont("helvetica", "normal");
            doc.text(tratamiento, 35, 135);

            //Firma
            doc.setFont("helvetica", "bold");
            doc.text('Firma:', 132, 200);
            doc.setFont("helvetica", "normal");
            doc.text('_______________________', 145, 200);

            // Generar el PDF y convertirlo en Blob 
            const pdfOutput = doc.output('blob');
            const blobUrl = URL.createObjectURL(pdfOutput);
            //Cierra el modal
            this.modalRef.hide();
            // Abrir el PDF en una nueva pestaña 
            window.open(blobUrl, '_blank');

          } else {
            this.alerta(result.message);
          }
        } catch (error) {
          console.error('Error POST:', error);
        }

        //Se detiene la ejecucion a espera de respuesta
        //Verifica que el registro del historial se registrara correctamente
        if (response.ok) {
          var result = await response.json();
          this.modalRef.hide()
          this.alerta("Receta registrada correctamente.");

        } else {
          this.alerta("Error al registrar la receta.");
        }
      } catch (error) {
        console.error('Error POST:', error);
      }
    } else {
      this.alerta("Llene todos los campos.");
    }
  }

  //Actualizar el estado de la cita
  //
  //
  modalEditar(updateModal: TemplateRef<any>, id_cita: string) {
    //Datos que se mostraran en el modal
    this.id_cita = id_cita;
    //Abre el modal
    this.modalRef = this.modalService.show(updateModal, { class: 'modal-xl' });//se le agrega la clase modal xl para hacer el modal grande
  }


  //Obtiene el historial clínico del paciente
  //
  //
  async infoHistorial() {
    //Obtiene todo el registro clinico del paciente
    try {
      const response = await fetch('http://localhost/api/historial/getinfo?id_paciente=' + this.id_paciente, {
        method: 'GET',
        credentials: 'include',
        headers: { 'Origin': window.location.origin }
      });

      //Se detiene la ejecucion a espera de respuesta
      var result = await response.json();
      this.historialSource.data = result;

    } catch (error) {
      console.error('Error POST:', error);
    }
  }

  //if (this.modalRef) { this.modalRef.hide(); }


  //Actualizar un registro de historial
  //El editar no permite eliminar el documento solo se podra remplazar
  //
  async update(event: Event, estado: string) {
    //Evita que se actualice la pagina
    event.preventDefault();

    //Se crea el objeto formdata para mandar los parametros
    var formData = new FormData();
    console.log(this.id_cita);
    console.log(estado);
    formData.append('id_cita', this.id_cita);
    formData.append('estado', estado);

    //Manda los datos del historial clinico
    try {
      //Actualizacion del registro
      var response = await fetch('http://localhost/api/consultorio/updatecita', {
        method: 'POST',
        body: formData,
        credentials: 'include',
        headers: { 'Origin': window.location.origin }
      });

      //Se obtiene el resultado
      //Verifica que el registro del historial se actualice correctamente
      if (response.ok) {
        var result = await response.json();
        this.modalRef.hide()
        this.informacion();
        this.alerta("Estado de la cita actualizado");
      } else {
        this.alerta(result.message);
      }
    } catch (error) {
      console.error('Error POST:', error);
    }
  }

  //Descarga del documento
  //No es necesario el id el documento ya cuenta con el id del paciente en el nombre ademas de la fecha hora y segundos en que se subio
  //Para evitar que el nombre se repita
  //
  //
  //
  async documento(event: Event, documento: string) {
    //Evita que se actualice la pagina
    event.preventDefault();
    try {
      const response = await fetch('http://localhost/api/historial/documento?documento=' + documento, {
        method: 'GET',
        credentials: 'include',
        headers: { 'Origin': window.location.origin }
      });//Peticion fetch

      //Se detiene la ejecucion a espera de respuesta
      this.dataSource.data = result;

      if (response.ok) {
        //Si la respuesta es correcta se obtiuene el blob del archivo
        var blob = await response.blob();
        //Se abre una ventana con el pdf
        window.open(URL.createObjectURL(blob), '_blank');

      } else {
        //Si la respuesta es diferente se obtiene la respuesta y se muestra el mensaje
        var result = await response.json();
        this.alerta(result.message)
      }

    } catch (error) {
      console.error('Error POST:', error);
    }
    this.informacion();
  }

  //Funcion para mandar la alertas con el snackbar de angular material
  //
  //
  alerta(mensaje: string) {
    //Muestra la alerta
    this.snackBar.open(mensaje, 'x', {
      duration: 7000,
      panelClass: 'custom-snackbar'
    });
  }

  calcularEdad(fechaNacimiento: string) {
    var fechaNac = new Date(fechaNacimiento);
    var hoy = new Date();

    //Se obtiene la edad restando el año actual al año de nacimiento
    var edad = hoy.getFullYear() - fechaNac.getFullYear();
    //
    var mes = hoy.getMonth() - fechaNac.getMonth();

    //Verifica decuardo a la fecha actual si la fecha de nacimiento el mes y dia no sean mayores si son a si se mantiene el año que se suma actual si no se resta
    if (mes < 0 || (mes === 0 && hoy.getDate() < fechaNac.getDate())) {
      edad--;
    }
    return '' + edad;
  }

}
